INSERT INTO `serv_faq` (`id`, `question`, `answers`, `faq_type`, `faq_cat`, `ord`, `status`) VALUES
(1, 'Creating Your Profile', '&lt;p&gt;To sign up on elance-clone.com and create your profile:&lt;br /&gt;\r\n1. Click on theSign up and Continue at the top of the elance-clone.com website.&lt;br /&gt;\r\n2. In sign up to create your Contractor account is to select the membership level.&lt;br /&gt;\r\n&lt;br /&gt;\r\nCongratulations, you have now joined the&amp;nbsp;elance-clonein online work! Next you will want to setup and customize your online profile. To finish customizing your profile please follow these steps:&lt;br /&gt;\r\n1. Go to the left navigation bar of&amp;nbsp;elance-clone&amp;nbsp;and click on My Profile.&lt;br /&gt;\r\n2. Complete your basic information.&lt;br /&gt;\r\n3. Then its time to get the profile started. On the next page you will upload your thumbnail photo, create a tagline, enter in a minimum hourly rate, create a contractor overview and enter in your skills.&lt;br /&gt;\r\nHere is what your completed profile will look like.&lt;/p&gt;\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/ckfinder/userfiles/images/elance-clone.png&quot; style=&quot;width: 400px; height: 548px;&quot; /&gt;&lt;/p&gt;', 'G', 2, 0, 'Y'),
(2, 'Creating Your Portfolio', '&lt;p&gt;\r\n	After you have completed your profile,you can edit your portfolio.&lt;br /&gt;\r\n	To edit your portfolio:&lt;br /&gt;\r\n	1. Click on upload Portfolio and upload your work there shown in the picture below.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;/ckfinder/userfiles/images/elance-clone_portfolio.png&quot; style=&quot;width: 400px; height: 202px;&quot; /&gt;&lt;/p&gt;\r\n', 'G', 2, 1, 'Y'),
(3, 'Look of Your Profile to Friends and Potential Clients  ', '&lt;p&gt;\r\n	Now after Completing the profile The look of the profile that Your Friends and Potential Clients can see is :&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n', 'G', 2, 2, 'Y'),
(22, 'Upgrading Membership', '&lt;p&gt;To upgrade your current membership:&lt;br /&gt;\r\n1. Click the Membership Plan button in the Dashboard.&lt;br /&gt;\r\n2. Click the Membership link on the left panel.&lt;br /&gt;\r\n3. Update your plan details. (The monthly cost will update at the bottom of the page)&lt;br /&gt;\r\n4. Click Submit.&lt;br /&gt;\r\n5. If appropriate, It will deduct amount from your current balance and update your membership.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;/tempimg/image/member.jpg&quot; width=&quot;600&quot; height=&quot;337&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;6. You can have auto membership plan updation by clicking on the checkbox.&lt;br /&gt;\r\n7. Later this auto membership plan updation could be edited through edit profile.&lt;/p&gt;', 'G', 6, 0, 'Y'),
(4, 'Searching the Jobs Index', '&lt;p&gt;After you have created your contractor profile,you can start submitting proposals&amp;nbsp;for jobs.&amp;nbsp;You can find and evaluate jobs by browsing the elance-clone&amp;nbsp;site or by receiving invitations from potential clients to submit proposals for their jobs.&lt;br /&gt;\r\nTo browse jobs:&lt;br /&gt;\r\n1. Go to ''Find Work'' on the top navigation bar and choose Search Jobs .&lt;br /&gt;\r\n2. You can browse jobs by a number of criteria:&lt;br /&gt;\r\nCategory&lt;br /&gt;\r\nLatest Job&lt;br /&gt;\r\nJob Name&lt;br /&gt;\r\n3.&amp;nbsp;Click the Job Name&amp;nbsp;to view a particular job description.&lt;br /&gt;\r\n4. For additional information about a client, click on client''s username.&lt;br /&gt;\r\nIn addition to actively browsing elance-clone&amp;nbsp;for jobs, you can also can create saved searches for jobs in your categories of interest and&amp;nbsp;receive daily email&amp;nbsp;notifications. For more information on , please click here.&lt;/p&gt;', 'G', 3, 0, 'Y'),
(8, 'Browsing Contractors ', '&lt;p&gt;Optionally, you can solicit bids from contractors by browsing their profiles and requesting proposals for your job. Note: It is not required that you request proposals from&amp;nbsp; contractors; you can also post a job for open bidding as outlined in the previous section.&lt;br /&gt;\r\n&amp;nbsp;To browse contractor profiles:&lt;br /&gt;\r\n1. On the home page, select the Hire in the top navigation bar and then click Search Contractors.&amp;nbsp; You can enter a keyword (EX: iPhone, PHP) and click the Search button to narrow your search results.&lt;br /&gt;\r\n2. You can filter the search results using the options in the left navigation. Sort by clicking on the dropdown to the right of Sort by: at the top of the page.&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;http://www.elance-clone.com/demo/singup.html&quot;&gt;http://www.elance-clone.com/demo/singup.html&lt;/a&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&lt;img src=&quot;/tempimg/image/rt.jpg&quot; width=&quot;600&quot; height=&quot;260&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;', 'G', 10, 0, 'Y'),
(6, 'Post a Job', '&lt;p&gt;To post jobs on Detect&amp;amp;Go.com, click the Project button in the upper left corner of the home page.&lt;br /&gt;\r\n1. Select PostProject option from the drop down menu&lt;br /&gt;\r\n2. First you will need to fill in your account details. Please fill out&amp;nbsp; fields marked * ,they are mandatory, if you have any questions along the way look for the tip that will appear to the right of each box for additional information.Click here to view post a job&amp;nbsp;&lt;a href=&quot;http://www.elance-clone.com/demo/postjob.html&quot;&gt;http://www.elance-clone.com/demo/postjob.html&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;data:image/png;base64,&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n3. Next you will be prompted to create your job post and then click continue. Remember to be as detailed and specific as you can.&lt;br /&gt;\r\n4. Enter a title for your job.&lt;br /&gt;\r\n5. Enter a job description. Be as detailed as possible, including specific information about the work needed, the size of the job, estimated start and completion dates, and some background on your company. You also have access to a number of job description templates by using our Description Assistant. If you select a template and you no longer want to use it, you can remove the template to revert back to your original text and selections by choosing the undo button.&lt;br /&gt;\r\n6. You may attach up to 10 files (maximum size 50 MB per attachment) that help clarify the job requirements and enable contractors to prepare more thorough, relevant proposals.&lt;br /&gt;\r\nPlease use discretion when including private information such as your web address or name.&lt;br /&gt;\r\n7. Select a Fixed price or Hourly job. Fixed price jobs are best when you have well-defined scope and structure for the work. If the job scope is less certain and you would like to work with your contractor on a more flexible basis, then choose the hourly option. Hourly jobs are Work View enabled. Work View is a safe and efficient way to pay for hourly work online. You will also be asked to select your budget. You will have the option to choose from a set of predefined budget ranges or you can create your own custom range.&lt;br /&gt;\r\n8. Select the category and subcategory that best describe your job and the skill set you would like your contractor to have.&lt;br /&gt;\r\nWhen you post a job in a subcategory, it will be viewed by all contractors in the main category.&lt;br /&gt;\r\n9. You may select up to 5 relevant skills you would like your contractor to have or groups that you would like your contractor to have joined. When you select skills, your job will be tagged with the associated keywords, making it more visible to qualified contractors when they are searching our job database.&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;', 'G', 9, 1, 'Y'),
(7, 'Signing Up with Elance-clone', '&lt;p&gt;To sign up on Elance-clone&amp;nbsp;and create your profile:&lt;br /&gt;\r\n1. Click on theSign up and Continue at the top of the Elance-clone.com website.&lt;br /&gt;\r\n2. In sign up to create your Contractor account is to select the membership level.&lt;br /&gt;\r\nCongratulations, you have now joined the Elance-clone&amp;nbsp;in online work! Next you will want to setup and customize your online profile. To finish customizing your profile please follow these steps:&lt;br /&gt;\r\n1. Go to the left navigation bar of Elance-clone&amp;nbsp;and click on My Profile.&lt;br /&gt;\r\n2. Complete your basic information.&lt;br /&gt;\r\n3. Then its time to get the profile started. On the next page you will upload your thumbnail photo, create a tagline, enter in a minimum hourly rate, create a contractor overview and enter in your skills.&lt;br /&gt;\r\nHere is what your completed profile will look like.&lt;/p&gt;', 'G', 9, 0, 'Y'),
(9, 'Requesting Proposals from Elance-clone Contractors', '&lt;p&gt;You can easily send requests to Detect&amp;amp;Go.com contractors to submit proposals for your job, either from search results or from their individual profiles.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&lt;img alt=&quot;&quot; src=&quot;/ckfinder/userfiles/images/User%20Profile%20krishna%20kushwaha.png&quot; style=&quot;width: 730px; height: 591px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Requesting contractor proposals from search results: You can invite a contractor by clicking on the Red Invite button. If you already have active postings, you will see a window listing all of your jobs that are open for bidding. You can either choose an existing job or create a new one. Each selected service contractor will receive an email invitation to submit their proposal for your job.&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;', 'G', 10, 1, 'Y'),
(17, 'Getting Hired', '&lt;p&gt;If the client selects your proposal and hires you for the job, the following will occur:&lt;/p&gt;\r\n&lt;ul&gt;\r\n    &lt;li&gt;You will receive an email notification informing you that you have been selected.&lt;/li&gt;\r\n    &lt;li&gt;The job can be found on your Dasboardl with Accept and Decline options.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p&gt;You must then accept the job selection on Elance-clone&amp;nbsp;before the job can begin.&lt;/p&gt;', 'G', 4, 0, 'Y'),
(18, 'Accepting Jobs', '&lt;p&gt;\r\n	To accept the job:&lt;br /&gt;\r\n	1. Click the Accept button on Dashboard .&lt;br /&gt;\r\n	2. Click on the link for accepting job in the email.&lt;/p&gt;\r\n', 'G', 4, 1, 'Y'),
(19, 'Sending Pay request to the Client', '&lt;p&gt;\r\n	To Request for Pay to the client:&lt;br /&gt;\r\n	1. Go to the Fund Management link in the left navigation and click Milestone.&lt;br /&gt;\r\n	2. Select the job for which you want to send an request and click on action and select the request .&lt;br /&gt;\r\n	3. Click on Submit.&lt;/p&gt;\r\n', 'G', 5, 0, 'Y'),
(20, 'Confirming Payment', '&lt;p&gt;\r\n	To Confirm the Payment:&lt;br /&gt;\r\n	1. Go to the Fund Management link in the left navigation and click Milestone.&lt;br /&gt;\r\n	2. Select the job for which you want to send an request and click on accept amount .&lt;br /&gt;\r\n	3. Click on Submit.&lt;/p&gt;\r\n', 'G', 5, 1, 'Y'),
(21, 'Withdrawing Funds', '&lt;p&gt;\r\n	To withdraw the Payment:&lt;br /&gt;\r\n	1. Go to the Fund Management link in the left navigation and click Withdraw Funds.&lt;br /&gt;\r\n	2. Select the paypal and give the details for the withdrawal of money.&lt;br /&gt;\r\n	3. Click on click here to withdraw.&lt;br /&gt;\r\n	4. Mention the required details.&lt;/p&gt;\r\n', 'G', 5, 2, 'Y'),
(29, 'Test Question?', '&lt;p&gt;\r\n	Test Answer&lt;/p&gt;\r\n', 'G', 18, 1, 'Y'),
(30, 'Testing Question ?', '&lt;p&gt;\r\n	Testing answer.&lt;/p&gt;\r\n', 'G', 18, 2, 'N');
