(2, 2, 'approval@sbtradeleads.com', 'Your offer has been Approved', 'Hi %fname%,    Your offer %offer_title% # %offer_id% has been approved for inclusion on the site.      Check now at %offer_url%    cat name: %category%  title:    %offer_title%  Id:       %offer_id%  URL:      %offer_url%      Regards,  Admin', 'yes', 'yes'),
(3, 3, 'disapproval@sbtradeleads.com', 'Your offer  has been rejected', 'Hi %fname%,    Your offer %offer_title% # %offer_id% has been rejected for inclusion on the site.       Regards,  Admin', 'yes', 'yes'),
(4, 4, 'password@sbtradeleads.com', 'Your password', '\r\nHi %fname%,    Here is your login information:    Username:        %username%  Password:        %password%  Email:           %email%      Thanks for being part of our website.    Regards,  Admin', 'yes', 'yes'),
(1, 1, 'welcome@sbtradeleads.com', 'Welcome to sbtradeleads.com', '\r\nHi %fname%,    Welcome to sbtradeleads.com.    Your registration information is as follows:  First:       %fname%  last:        %lname%  Username:    %username%  Password:    %password%  Email:       %email%  Login:       %login_url%    Thanks for being part of our website.    Regards,  Admin  Login url is %login_url%\r\n', 'yes', 'no'),
(7, 7, 'automail@sbtradeleads.com', 'Member has Posted a offer and waiting your approval', 'Dear Admin    A new offer %offer_title% has just been posted by %username%at sbtradeleads.com    title: %offer_title%  Id:    %offer_id%  url:   %offer_url%      Regards   Automail', 'yes', 'yes'),
(6, 6, 'admin@sbtradeleads.com', 'New offer posted has been sent for admin approval', 'Hi %fname%,     Your offer %offer_title% has been posted at sbtradeleads.com.    title: %offer_title%  Id:    %offer_id%  URL:   %offer_url%    Regrds  Admin', 'yes', 'yes'),
(10, 10, 'admin@sbtradeleads.com', 'Comments posted to Admin', 'Hi %visitor_name%,            Your comments have been posted to the Admin at sbtradeleads.com.    Details of comments are  visitor: %visitor_name%  message date: %message_date%  message title: %message_title%  message text: %message_text%    Regards  Admin', 'yes', 'yes'),
(11, 11, 'automail@sbtradeleads.com', 'User Posted Comments', 'Hi Admin,    %visitor_name% has posted some comments at sbresource.com.    Details of comments are  visitor: %visitor_name%  message date: %message_date%  message title: %message_title%  message text: %message_text%    ards  Automail', 'yes', 'yes'),
(22, 22, 'confirmation@sbtradeleads.com', 'Your confirmation link to sbtradeleads.com', 'Hi,    Welcome to sbtradeleads.com.    Your confirmation link is given below :    Link:  %signup_url%  Email: %email%    Thanks for being part of our website.    Regards,  Admin', 'yes', 'no'),
(23, 23, 'notification@sbtradeleads.com', 'You just received a message at sbtradeleads.com', 'Hi %fname%,    Details of message are  sender:  %sender_username%  message date: %message_date%  message title: %message_title%  message text: %message_text%      Thanks for being part of our website.    Regards,  Admin', 'yes', 'no'),
(25, 25, 'admin@sbtradeleads.com', 'Upgrade Membership', 'Hi %fname%,    To complete your request for upgrade from %current_level% to %upgrade_level% please deposit the upgradeation fee %upgrade_fee% via paypal              Thanks.    Regrds  Admin', 'yes', 'yes'),
(24, 24, 'admin@sbtradeleads.com', 'New offer  has been posted in your favorite category', 'Hi %fname%,    New offer %offer_title% has been posted at sbtradeleads.com. in your favorite category %category%.    cat name: %category%  title:    %offer_title%  Id:       %offer_id%  url:      %offer_url%    Regrds  Admin', 'yes', 'yes'),
(26, 26, 'admin@sbtradeleads.com', 'Account deletion notification', 'Hi <fname>,    You have not logged in at sbtradeleads for quite a some time. So your account might be deleted if you didn&#39;t log-in soon.             Thanks.    Regrds  Admin', 'yes', 'yes');
